import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'
import { ListGroup } from 'react-bootstrap'
import { useState } from 'react'
import Modal from 'react-bootstrap/Modal'
import classes from './Work.module.css'

const Work = () => {
  const [modalState, setModalState] = useState(false);

  // const [modalState, setModalState] = useState < "modal-one" | "modal-two" | "close" > ("close")

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  // const handleShowModalOne = () => {
  //   setModalState("modal-one")
  // }

  // const handleShowModalTwo = () => {
  //   setModalState("modal-two")
  // }

  // const handleShowModalThree = () => {
  //   setModalState("modal-three")
  // }

  // const handleClose = () => {
  //   setModalState("close")
  // }


  return (
    <>
      <Card style={{
        margin: 'auto',
        marginTop: '20px',
        width: '400px',
        height: '250px'
      }} bg="light" border="light" text="black" className="text-center">
        <Card.Header><b>Work Experience</b></Card.Header>
        <Card.Body>
          <Card.Title></Card.Title>
          <Card.Text>
            <ListGroup>
              <ListGroup.Item action variant="dark" onClick={handleShowModalOne === "modal-one"}>HP</ListGroup.Item>
              <ListGroup.Item action variant="dark" onClick={handleShowModalTwo === "modal-two"}>HP Enterprise</ListGroup.Item>
              <ListGroup.Item action variant="dark" onClick={handleShowModalThree === "modal-three"}>IBM</ListGroup.Item>
            </ListGroup>
          </Card.Text>
          {/* <Button href="/" variant="primary">Home</Button> */}
        </Card.Body>
      </Card >
      <Modal show={modalState === "modal-one"} onHide={handleClose} centered={true} >
        {/* <Modal show={show} onHide={handleClose} centered={true} > */}
        <Modal.Header>
          <div className={classes.modalHPtitle}>
            <h4>HP</h4>
          </div>
        </Modal.Header>
        <Modal.Body className="text-center"><p><b>Program Coordinator - Worlwide Commercial Notebooks</b> <p></p>
          <p>Mainly in charge of Bill Of Material for Sustaining and New Product Introductions</p></p></Modal.Body>
        <Modal.Footer>
          <Button variant="danger" size="sm" onClick={handleClose} style={{ margin: 'auto' }}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
      <Modal show={modalState === "modal-two"} onHide={handleClose} centered={true} >
        <Modal.Header>
          <div className={classes.modalHPtitle}>
            <h4>HP Enterprise</h4>
          </div>
        </Modal.Header>
        <Modal.Body className="text-center"><p><b>Payroll - US Region</b> <p></p>
          <p>Payroll anaylist for US Region</p></p></Modal.Body>
        <Modal.Footer>
          <Button variant="danger" size="sm" onClick={handleClose} style={{ margin: 'auto' }}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
      <Modal show={modalState === "modal-three"} onHide={handleClose} centered={true} >
        <Modal.Header>
          <div className={classes.modaltitle}>
            <h4>IBM</h4>
          </div>
        </Modal.Header>
        <Modal.Body className="text-center"><p><b>Accounts Receivable - US Region</b> <p></p>
          <p>Mainly in charge of Bill Of Material for Sustaining and New Product Introductions</p></p></Modal.Body>
        <Modal.Footer>
          <Button variant="danger" size="sm" onClick={handleClose} style={{ margin: 'auto' }}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default Work;


