import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'
import { ListGroup } from 'react-bootstrap'
import { useState } from 'react'
import Modal from 'react-bootstrap/Modal'
import classes from './Work.module.css'
import MyModals from './MyModals'

const Work = () => {
  const [modalState, setModalState] = useState < "modal-one" | "modal-two" | "close" > ("close");

  // const handleClose = () => setShow(false);
  // const handleShow = () => setShow(true);

  const handleShowModalOne = () => {
    setModalState("modal-one")
  }

  const handleShowModalTwo = () => {
    setModalState("modal-two")
  }

  const handleShowModalThree = () => {
    setModalState("modal-three")
  }

  const handleClose = () => {
    setModalState("close")
  }


  return (
    <>
      <Card style={{
        margin: 'auto',
        marginTop: '20px',
        width: '400px',
        height: '250px'
      }} bg="light" border="light" text="black" className="text-center">
        <Card.Header><b>Work Experience</b></Card.Header>
        <Card.Body>
          <Card.Title></Card.Title>
          <Card.Text>
            <ListGroup>
              <ListGroup.Item action variant="dark" onClick={handleShowModalOne === "modal-one"}>HP</ListGroup.Item>
              <ListGroup.Item action variant="dark" onClick={handleShowModalTwo === "modal-two"}>HP Enterprise</ListGroup.Item>
              <ListGroup.Item action variant="dark" onClick={handleShowModalThree === "modal-three"}>IBM</ListGroup.Item>
            </ListGroup>
          </Card.Text>
          {/* <Button href="/" variant="primary">Home</Button> */}
        </Card.Body>
      </Card >

    </>
  );
}

export default Work;


